import React, { useEffect, useContext,useState} from 'react';
import Slider from "react-slick";
import './index.css';

import Slide1 from '../../../../assets/images/slider-1.png';
import Slide2 from '../../../../assets/images/slider-2.png';
import Button from '@mui/material/Button';

import Newsletter from '../../../common/newsletter';

import { UserContext } from "../../../../pages/UserPage";
import api from '../../../../api/api';
import { baseURL_ } from '../../../../utils/env';

const HomeSlider = () => {
    const [dataIMG, setDataIMG] = useState([]);
    const context = useContext(UserContext);
  
    useEffect(() => {
      listIMG();
    }, []);
  
    const listIMG = async () => {
      let arr = [];
      const list = await api.get(`${baseURL_}/imgshose`);
      const findImage = list.data.data.map((img) => img.images).flat();
      for (let i = 0; i < 3; i++) {
        const randomIndex = Math.floor(Math.random() * findImage.length);
        arr.push(findImage[randomIndex]);
      }
      console.log('arr',arr)
      setDataIMG(arr);
    };


    var settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        fade: true,
        arrows: context.windowWidth>992 ? true : false,
        autoplay:true
    };



    return (
        <section className='homeSlider'>
            <div className='container-fluid position-relative'>
                <Slider {...settings} className='home_slider_Main'>
                    <div className="item">
                        <img src={Slide1} className='w-100' />
                        <div className='info'>
                            <h2 class="mb-4">
                                Donâ€™t miss amazing<br />
                                grocery deals
                            </h2>
                            <p>Sign up for the daily newsletter</p>
                        </div>
                    </div>
                    <div className="item">
                        <img src={Slide2} className='w-100' />
                        <div className='info'>
                            <h2 class="mb-3">
                                Fresh Vegetables<br />
                                Big discount
                            </h2>
                            <p>Sign up for the daily newsletter</p>
                        </div>
                    </div>
                </Slider>

                {
                    context.windowWidth>992 && <Newsletter/>
                }
                

            </div>
        </section>
    )
}

export default HomeSlider;
