import React, { useEffect, useContext, useState,useRef } from "react";
import Slider from "react-slick";
import "./index.css";

import Slide1 from "../../../../assets/images/slider-1.png";
import Slide2 from "../../../../assets/images/slider-2.png";
import Button from "@mui/material/Button";

import Newsletter from "../../../common/newsletter";

import { UserContext } from "../../../../pages/UserPage";
import api from "../../../../api/api";
import { baseURL_, baseIMG_ } from "../../../../utils/env";

const HomeSlider = () => {
  const [dataIMG, setDataIMG] = useState([]);
  const context = useContext(UserContext);
  const [currentSlide, setCurrentSlide] = useState(0); // State để lưu index của slide hiện tại
  const sliderRef = useRef(null); // Ref để tham chiếu tới Slider


  useEffect(() => {
    listIMG();
  }, []);

  const listIMG = async () => {
    let arr = [];
    const list = await api.get(`${baseURL_}/imgshose`);
    const findImage = list.data.data.map((img) => img.images).flat();
    for (let i = 0; i < 3; i++) {
      const randomIndex = Math.floor(Math.random() * findImage.length);
      arr.push(findImage[randomIndex]);
    }
    console.log("arr", arr);
    setDataIMG(arr);
  };

  var settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    // fade: true,
    arrows: false,
    autoplay: true,
    afterChange: (index) => setCurrentSlide(index),

  };
//   useEffect(() => {
//     const interval = setInterval(() => {
//       if (sliderRef.current) {
//         sliderRef.current.slickNext();
//       }
//     }, 5000); 

//     return () => clearInterval(interval);
//   }, []);



  return (
    <section className="homeSlider">
      <div className="container-fluid position-relative">
        <Slider {...settings} className="home_slider_Main">
          {dataIMG.map((img, index) => (
            <div key={index} className="item">
              <div className="image-merge-container">
                <div className="left-image">
                  <img
                    src={`${baseURL_}/resize?url=` + img + "&width=500&height=600"}
                    alt="Left Image"
                    className="image"
                  />
                </div>
                <div className="right-image">
                  <img
                    src={`${baseURL_}/resize?url=` + img + "&width=500&height=600"}
                    alt="Right Image"
                    className="image"
                  />
                </div>
              </div>
              <div className="info">
                <h2 className="mb-4">
                  Don’t miss amazing
                  <br />
                  grocery deals
                </h2>
                <p>Sign up for the daily newsletter</p>
              </div>
            </div>
          ))}
        </Slider>

        {context.windowWidth > 992 && <Newsletter />}
      </div>
    </section>
  );
};

export default HomeSlider;
