import React, { useEffect, useState, createContext, useContext } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../components/user/user.css";
import "../responsive.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";

import UserHeader from "../components/common/Header/UserHeader";
import Footer from "../components/common/footer/footer";
import Home from "../components/user/Home/index";
import About from "../components/user//About/index";
import Listing from "../components/user//Listing";
import NotFound from "./NotFound";
import DetailsPage from "../components/user/Details";
import Checkout from "../components/user/checkout";
// import axios from "axios";
import Cart from "../components/user/cart";
// import SignIn from "./pages/SignIn";
// import SignUp from "./pages/SignUp";
import Loader from "../assets/images/loading.gif";
import api from "../api/api"
import {AdminContext} from '../App'

const UserContext = createContext();

const UserPage = () => {
  const context=useContext(AdminContext)
  const [productData, setProductData] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [categoriData, setCategoriData] = useState([]);
  const [isLoading, setIsloading] = useState(true);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  const [isopenNavigation, setIsopenNavigation] = useState(false);

  const [isLogin, setIsLogin] = useState(true);
  const [isOpenFilters, setIsopenFilters] = useState(false);

  const [cartTotalAmount, setCartTotalAmount] = useState();

  useEffect(() => {
    getDataProduct('http://192.168.10.110:6969/v1/shose');
    getDataCateGori('http://192.168.10.110:6969/v1/categori');
    //  getCartData("http://localhost:3000/cartItems");

    // const is_Login = localStorage.getItem("isLogin");

    // setIsLogin(is_Login);
    setIsLogin(true);

    setTimeout(() => {
      // setProductData(data[1]);
      setIsloading(false);
    }, 3000);
  }, []);
  useEffect(() => {
    context.setisHideSidebarAndHeader(true);
  }, [context]);
  console.log('productData',productData)
  console.log('categoriData',categoriData)


  const getCartData = async (url) => {
    try {
      await api.get(url).then((response) => {
        setCartItems(response.data);
      });
    } catch (error) {
      console.log(error.message);
    } };
  const getDataCateGori = async (url) => {
    try {
      await api.get(url).then((response) => {
        if(response.data.status){
          setCategoriData(response.data.data);
        }
      });
    } catch (error) {
      console.log(error.message);
    }
  }
  const getDataProduct = async (url) => {
    try {
      await api.get(url).then((response) => {
        console.log('responseresponse',response.data)
        if(response.data.status){
          setProductData(response.data.data);

        }
      });
    } catch (error) {
      console.log(error.message);
    }
  };

  // const addToCart = async (item) => {
  //   item.quantity = 1;

  //   try {
  //     await api.post("http://localhost:5000/cartItems", item).then((res) => {
  //       if (res !== undefined) {
  //         setCartItems([...cartItems, { ...item, quantity: 1 }]);
  //       }
  //     });
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  // const removeItemsFromCart = async (id) => {
  //   const response = await api.delete(
  //     `http://localhost:3000/cartItems/${id}`
  //   );
  //   if (response !== null) {
  //     getCartData("http://localhost:3000/cartItems");
  //   }
  // };

  // const emptyCart = () => {
  //   setCartItems([]);
  // };

  // const signIn = () => {
  //   const is_Login = localStorage.getItem("isLogin");
  //   setIsLogin(is_Login);
  // };

  // const signOut = () => {
  //   localStorage.removeItem("isLogin");
  //   setIsLogin(false);
  // };

  // const openFilters = () => {
  //   setIsopenFilters(!isOpenFilters);
  // };

  const value = {
    cartItems,
    isLogin,
    windowWidth,
    isOpenFilters,
    // addToCart,
    // removeItemsFromCart,
    // emptyCart,
    // signOut,
    // signIn,
    // openFilters,
    isopenNavigation,
    setIsopenNavigation,
    setCartTotalAmount,
    cartTotalAmount,
    setCartItems,
    cartItems,
  };

  // console.log('data.productData.length',value)
  return (
    // data.productData.length !== 0 && (
        <UserContext.Provider value={value}>
          {isLoading === true && (
            <div className="loader">
              <img src={Loader} />
            </div>
          )}

          {/* <UserHeader data={data.productData} /> */}
          <Routes>
            {/* <Route
              exact={true}
              path="/"
              element={<Home categoriData={categoriData} productData={productData} />}
              // element={<Home data={data.productData} />}
            /> */}
            {/* <Route
              exact={true}
              path="/cat/:id"
              element={<Listing categoriData={categoriData}  single={true} />}
            /> */}
            {/* <Route
              exact={true}
              path="/cat/:id/:id"
              element={<Listing data={data.productData} single={false} />}
            /> */}
            {/* <Route
              exact={true}
              path="/product/:id"
              element={<DetailsPage productData={productData} />}
            /> */}
            {/* <Route exact={true} path="/cart" element={<Cart />} />
            <Route exact={true} path="/signIn" element={<SignIn />} />
            <Route exact={true} path="/signUp" element={<SignUp />} />
            <Route exact={true} path="/checkout" element={<Checkout />} /> */}
            <Route exact={true} path="*" element={<NotFound />} />
          </Routes>
          <Footer />
        </UserContext.Provider>
    // )
  );
}


export default UserPage;
export { UserContext };
