// src/components/Chat.js
import React, { useEffect, useState } from 'react';
import socket from '../../../services/socket';
import { io } from 'socket.io-client';


const Chat = () => {
  const [message, setMessage] = useState('');
//   const [chat, setChat] = useState([]);
  const [userID, setUserID] = useState('');
  const [receiverID, setReceiverID] = useState('');
  const [socket, setSocket] = useState<SocketIOClient.Socket | null>(null);

  const [receivedMessages, setReceivedMessages] = useState([]);


  useEffect(() => {
    const newSocket = io('http://192.168.10.110:6969'); // Thay đổi địa chỉ và port tương ứng với server của bạn
    setSocket(newSocket);
    newSocket.emit('register', userID);

    newSocket.on('receiveMessage', (data) => {
        setReceivedMessages((prevMessages) => [...prevMessages, data]);
      });


    // return () => {
    //   socket.off('receiveMessage');
    // };
    return () => {
        newSocket.disconnect();
      };
  }, [userID]);

  const handleSendMessage = () => {
    socket.emit('sendMessage', { senderID: userID, receiverID, message });
    setChat(prevChat => [...prevChat, { senderID: userID, message }]);
    setMessage('');
  };

  return (
    <div>
      <h1>Chat</h1>
      <div>
        <input
          type="text"
          placeholder="Your ID"
          value={userID}
          onChange={e => setUserID(e.target.value)}
        />
        <input
          type="text"
          placeholder="Receiver ID"
          value={receiverID}
          onChange={e => setReceiverID(e.target.value)}
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Message"
          value={message}
          onChange={e => setMessage(e.target.value)}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
      <div>
        {chat.map((msg, index) => (
          <div key={index}>
            <strong>{msg.senderID}</strong>: {msg.message}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Chat;


