import React, { useEffect, useState } from "react";
import Banner1 from "../../../assets/images/banner1.jpg";
import Banner2 from "../../../assets/images/banner2.jpg";
import Banner3 from "../../../assets/images/banner3.jpg";

import "./style.css";
import api from "../../../api/api";
import { baseURL_, baseIMG_ } from "../../../utils/env";

const Banners = () => {
  const [dataIMG, setDataIMG] = useState([]);

  useEffect(() => {
    listIMG();
  }, []);

  const listIMG = async () => {
    let arr = [];
    const list = await api.get(`${baseURL_}/imgshose`);
    const findImage = list.data.data.map((img) => img.images).flat();
    for (let i = 0; i < 3; i++) {
      const randomIndex = Math.floor(Math.random() * findImage.length);
      arr.push(findImage[randomIndex]);
    }
    // console.log('imgshose',arr)
    setDataIMG(arr);
  };
  console.log("dataIMG", dataIMG);

  return (
    <div className="bannerSection">
      <div className="container-fluid">
        <div className="row">
          {dataIMG &&
            dataIMG.length > 0 &&
            dataIMG.map((item, index) => (
              <div className="col" key={index}>
                <div className="box">
                  <img
                    src={
                      `${baseURL_}/resize?url=` + item + "&width=200&height=100"
                    }
                    className="w-100 transition"
                    style={{ objectFit: "cover" }}
                    alt={`Image ${index}`}
                  />
                  <span className="new-label">NEW</span>
                </div>
              </div>
            ))}

          {/* <div className="col">
              <div className="box">
                <img src={Banner2} className="w-100 transition" />
              </div>
            </div>

            <div className="col">
              <div className="box">
                <img src={Banner3} className="w-100 transition" />
              </div>
            </div> */}
        </div>
      </div>
    </div>
  );
};

export default Banners;
