import React, { useEffect } from "react";
import Banner1 from "../../../assets/images/banner1.jpg";
import Banner2 from "../../../assets/images/banner2.jpg";
import Banner3 from "../../../assets/images/banner3.jpg";

import "./style.css";
import api from "../../../api/api";
import { baseURL_ } from "../../../utils/env";


const Banners = () => {
  
  useEffect(()=>{
    listIMG()

  },[])

  const listIMG=async()=>{
    const list= await api.get(`${baseURL_}/imgshose`)
    const findImage=list.data.data.map(img=>img.images).flat()
const randomIndex=Math.floor(Math.random()*findImage.length)
     console.log('imgshose',randomIndex)
  }

  return (
      <div className="bannerSection">
        <div className="container-fluid">
          <div className="row">
            <div className="col">
              <div className="box">
                <img src={Banner1} className="w-100 transition" />
              </div>
            </div>

            <div className="col">
              <div className="box">
                <img src={Banner2} className="w-100 transition" />
              </div>
            </div>

            <div className="col">
              <div className="box">
                <img src={Banner3} className="w-100 transition" />
              </div>
            </div>
          </div>
        </div>
      </div>
  );
};

export default Banners;
